mapper.select:
SELECT p.post_id AS postId, p.post_writer AS postWriter, p.post_title AS postTitle, p.post_content AS postContent, c.category_id AS categoryId, p.hits AS postHits, p.upload_datetime AS uploadDatetime, p.update_datetime AS updateDatetime FROM post p JOIN category c ON c.category_id = p.category_id
AND c.category_id = #{categoryId}
AND ( p.post_title LIKE CONCAT('%', #{keyword}, '%') OR p.post_writer LIKE CONCAT('%', #{keyword}, '%') OR p.post_content LIKE CONCAT('%', #{keyword}, '%') )
AND DATE(p.upload_datetime) BETWEEN DATE(#{startDate}) AND DATE(#{endDate})
ORDER BY p.upload_datetime DESC LIMIT #{offset}, #{boardPerPage} ;
